#define ECLIPSE_LOGO_WIDTH 128
#define ECLIPSE_LOGO_HEIGHT 44

// Draw bitmap helper function
void draw_bitmap(int16_t x, int16_t y, const unsigned char* bitmap, int16_t w, int16_t h)
{
    int16_t byteWidth = (w + 7) / 8;
    uint8_t byte = 0;

    for(int16_t j = 0; j < h; j++) {
        for(int16_t i = 0; i < w; i++) {
            if(i & 7) byte <<= 1;
            else      byte = bitmap[j * byteWidth + i / 8];
            
            if(byte & 0x80) {
                ssd1306_DrawPixel(x + i, y + j, White);
            }
        }
    }
}

void screen_draw_smiley(void)
{
    ssd1306_Fill(Black);
    
    // Display Eclipse Foundation logo bitmap (128x44 pixels, centered)
    draw_bitmap(0, 10, eclipse_logo_bitmap, ECLIPSE_LOGO_WIDTH, ECLIPSE_LOGO_HEIGHT);
    
    ssd1306_UpdateScreen();
}

// Happy smiley :)
void screen_draw_smiley_happy(void)
{
    ssd1306_Fill(Black);
    
    // Cercle extérieur (visage)
    ssd1306_DrawCircle(64, 32, 20, White);
    
    // Yeux heureux (cercles remplis)
    ssd1306_DrawCircle(58, 26, 2, White);
    ssd1306_DrawCircle(70, 26, 2, White);
    
    // Grand sourire courbe
    for(int x = 52; x <= 76; x++)
    {
        int y = 38 + (x - 64) * (x - 64) / 80;
        ssd1306_DrawPixel(x, y, White);
    }
    
    // Texte
    ssd1306_SetCursor(2, L2);
    ssd1306_WriteString("allais acheter", Font_11x18, White);
    ssd1306_SetCursor(2, L3);
    ssd1306_WriteString("chez lucas.com", Font_11x18, White);
    
    ssd1306_UpdateScreen();
}

